package testStrings;

public class SwapTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10,b=20;
		/*
		 * int temp; temp=a; a=b; b=temp;//Third Variable
		 */
		
		//With out Third Variable
		a=a+b;//30
		b=a-b;//10
		a=a-b;//20
		
		System.out.println("a="+a);
		System.out.println("b="+b);
		

	}

}
